let resultado,expresion=document.getElementById("expresion");const comprobar_expresion=(e,r)=>{try{const t=new RegExp(e),o=new RegExp(e,"g"),s=r.match(o);return t.test(r)?["success","Texto es válido",s]:["error","Texto no es válido"]}catch(e){return["error","Error en la expresión regular"]}};document.getElementById("texto").addEventListener("input",e=>{resultado=comprobar_expresion(expresion.value,e.target.value),"success"==resultado[0]?document.getElementById("resultado").innerHTML=`<div class="alert alert-success" role="alert">${resultado[1]}. Coincidencias: ${resultado[2].length}. Valores: ${resultado[2].join(", ")} </div>`:"error"==resultado[0]&&(document.getElementById("resultado").innerHTML=`<div class="alert alert-danger" role="alert">${resultado[1]}</div>`)});